import React from 'react';
import './CourseCard.css'; // Optional: Add CSS for styling

function CourseCard({ title, description, duration, instructor, rating }) {
  return (
    <div className="course-card">
      <h2>{title}</h2>
      <p>{description}</p>
      <p><strong>Duration:</strong> {duration}</p>
      <p><strong>Instructor:</strong> {instructor}</p>
      <p><strong>Rating:</strong> {rating} &#9733;</p>
      <button className="enroll-btn">Enroll Now</button>

      {/* Example of adding more details */}
      <div className="course-details">
        <h4>Course Topics</h4>
        <ul>
          <li>Introduction to {title}</li>
          <li>Key Concepts</li>
          <li>Hands-on Projects</li>
          {/* Add more topics here */}
        </ul>
      </div>
    </div>
  );
}

export default CourseCard;
