import React, { useState } from "react";

function ProfilePage() {
  const [user, setUser] = useState({
    name: "Bhavana",
    email: "bhavana@example.com",
    bio: "Passionate about learning and exploring new technologies!",
    profilePic: "https://via.placeholder.com/100",
  });

  const [darkMode, setDarkMode] = useState(false);

  // Handle profile picture upload
  const handleProfilePicChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUser({ ...user, profilePic: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  // Edit Name & Bio
  const handleEdit = () => {
    const newName = prompt("Enter new name:", user.name);
    const newBio = prompt("Enter new bio:", user.bio);
    if (newName && newBio) {
      setUser({ ...user, name: newName, bio: newBio });
    }
  };

  // Toggle Dark Mode
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={`profile-container ${darkMode ? "dark-mode" : ""}`}>
      <h1>üë§ Your Profile</h1>
      <img src={user.profilePic} alt="Profile" className="profile-pic" />
      <input type="file" accept="image/*" onChange={handleProfilePicChange} />

      <h2>{user.name}</h2>
      <p>Email: {user.email}</p>
      <p>Bio: {user.bio}</p>

      <button onClick={handleEdit}>‚úè Edit Profile</button>
      <button onClick={toggleDarkMode}>
        {darkMode ? "‚òÄ Light Mode" : "üåô Dark Mode"}
      </button>
    </div>
  );
}

export default ProfilePage;
