import React, { useState } from "react";

function ProfilePage() {
  const [user, setUser] = useState({
    name: "Bhavana",
    email: "bhavana@example.com",
    bio: "Passionate about learning and exploring new technologies!",
    profilePic: "https://via.placeholder.com/100",
    password: "********", // Default hidden password
  });

  const [darkMode, setDarkMode] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // Handle profile picture upload
  const handleProfilePicChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUser({ ...user, profilePic: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  // Edit Name & Bio
  const handleEdit = () => {
    const newName = prompt("Enter new name:", user.name);
    const newBio = prompt("Enter new bio:", user.bio);
    if (newName && newBio) {
      setUser({ ...user, name: newName, bio: newBio });
    }
  };

  // Toggle Dark Mode
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  // Change Password
  const handleChangePassword = () => {
    const newPassword = prompt("Enter new password:");
    if (newPassword && newPassword.length >= 6) {
      setUser({ ...user, password: newPassword });
      alert("Password changed successfully!");
    } else {
      alert("Password must be at least 6 characters long.");
    }
  };

  return (
    <div className={`profile-container ${darkMode ? "dark-mode" : ""}`}>
      <h1>üë§ Your Profile</h1>
      <img src={user.profilePic} alt="Profile" className="profile-pic" />
      <input type="file" accept="image/*" onChange={handleProfilePicChange} />

      <h2>{user.name}</h2>
      <p>Email: {user.email}</p>
      <p>Bio: {user.bio}</p>

      {/* Password Section */}
      <div className="password-section">
        <p>Password: {showPassword ? user.password : "********"}</p>
        <button onClick={() => setShowPassword(!showPassword)}>
          {showPassword ? "üôà Hide" : "üëÄ Show"}
        </button>
        <button onClick={handleChangePassword}>üîë Change Password</button>
      </div>

      <button onClick={handleEdit}>‚úè Edit Profile</button>
      <button onClick={toggleDarkMode}>
        {darkMode ? "‚òÄ Light Mode" : "üåô Dark Mode"}
      </button>
    </div>
  );
}

export default ProfilePage;
