import React, { useState } from 'react';
import CourseCard from '../components/CourseCard';
import './CoursePage.css'; // Ensure you've added the CSS for styling

function CoursePage() {
  // Sample courses data
  const [courses] = useState([
    {
      title: 'Course 1',
      description: 'Learn React',
      duration: '4 weeks',
      instructor: 'John Doe',
      rating: 4.5,
    },
    {
      title: 'Course 2',
      description: 'Learn JavaScript',
      duration: '3 weeks',
      instructor: 'Jane Smith',
      rating: 4.7,
    },
    {
      title: 'Course 3',
      description: 'Learn Python',
      duration: '6 weeks',
      instructor: 'Alice Brown',
      rating: 4.8,
    },
    // Add more courses as needed
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('title');
  const [visibleCourses, setVisibleCourses] = useState(3);

  // Filter courses based on the search term
  const filteredCourses = courses.filter(course =>
    course.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    course.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sort courses based on selected option
  const sortedCourses = [...filteredCourses].sort((a, b) => {
    if (sortOption === 'rating') {
      return b.rating - a.rating; // Sort by rating descending
    }
    if (sortOption === 'duration') {
      return a.duration.localeCompare(b.duration); // Sort by duration alphabetically
    }
    return a.title.localeCompare(b.title); // Default sort by title
  });

  // Load More functionality
  const loadMoreCourses = () => {
    setVisibleCourses(visibleCourses + 3);
  };

  return (
    <div className="course-page">
      <h1>Our Courses</h1>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search Courses..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-bar"
      />

      {/* Sort Dropdown */}
      <select onChange={(e) => setSortOption(e.target.value)} value={sortOption}>
        <option value="title">Sort by Title</option>
        <option value="rating">Sort by Rating</option>
        <option value="duration">Sort by Duration</option>
      </select>

      {/* Render courses dynamically */}
      <div className="course-list">
        {sortedCourses.slice(0, visibleCourses).map((course, index) => (
          <CourseCard 
            key={index} 
            title={course.title} 
            description={course.description} 
            duration={course.duration} 
            instructor={course.instructor} 
            rating={course.rating} 
          />
        ))}
      </div>

      {/* Load More Button */}
      {visibleCourses < sortedCourses.length && (
        <button onClick={loadMoreCourses}>Load More</button>
      )}
    </div>
  );
}

export default CoursePage;
