import React from 'react';
import './CourseCard.css'; // Ensure styles are updated accordingly

function CourseCard({ title, description, duration, instructor, rating, image, price, discount, topics }) {
  return (
    <div className="course-card">
      <img src={image} alt={title} className="course-image" />
      <h2>{title}</h2>
      <p>{description}</p>
      
      <div className="course-info">
        <p><strong>Duration:</strong> {duration}</p>
        <p><strong>Instructor:</strong> {instructor}</p>
        <p><strong>Rating:</strong> {rating} &#9733;</p>
      </div>

      <div className="pricing">
        <p className="price"><strong>Price:</strong> ${price}</p>
        {discount && <p className="discount">Discount: {discount}% off</p>}
      </div>

      <div className="course-details">
        <h4>Course Topics</h4>
        <ul>
          {topics.map((topic, index) => (
            <li key={index}>{topic}</li>
          ))}
        </ul>
      </div>

      <div className="action-buttons">
        <button className="enroll-btn">Enroll Now</button>
        <button className="share-btn">Share</button>
      </div>
    </div>
  );
}

export default CourseCard;
